        </div>)
        }
        else{
            return(<div>
                  <Card className={classes.card}>
      <CardContent>
        <Typography >
        <HomeComponent handleUserDetails={this.handleUserDetails} handleUserSignInDetails={this.handleUserSignInDetails}/>
