import React from 'react';
import PropTypes from 'prop-types';

import { withStyles } from '@material-ui/core/styles';
import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';
import TextField from '@material-ui/core/TextField';
import './MainComponent.css';
import NotesListComponent from './NotesListComponent';

const styles = theme => ({
  container: {
    marginLeft:300,
    marginRight:100,
    marginTop:150,
    display: 'flex',
    flexWrap: 'wrap',
  },
  fab: {
    margin: theme.spacing.unit,
    marginLeft:450,

  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
  },
  dense: {
    margin: 16,
  },
  menu: {
    width: 200,
  },
});


class InputNotes extends React.Component {
 constructor(){
   super()
  this.state = {
    arrayList:[],
    value: ''  
  }
  this.handleAdd=this.handleAdd.bind(this)
  this.handleChange=this.handleChange.bind(this)
 }
  handleChange(event) {
    this.setState({
      value: event.target.value,
    });
  }
  handleForCallback(task){
    const newState=[{
        id:Math.random()*3402938,
        value:task
    }]
    this.setState((currState)=>({
        arrayList:currState.arrayList.concat(newState)
    }))        
    console.log(this.state.arrayList)         

}

  handleAdd(){
    alert(this.state.value)
    this.setState({ value:""})

  }
  render() {
    const { classes } = this.props;

    return (
      <div>
      <form className={classes.container} noValidate autoComplete="off">
       <TextField
          onChange={this.handleChange}
          id="outlined-full-width"
          label="Label"
          value={this.state.value}
          style={{ margin: 8 }}
          placeholder="Take a note..."
          fullWidth
          margin="normal"
          variant="outlined"
          InputLabelProps={{
            shrink: true,
          }}
        />
        <Fab color="primary" aria-label="Add" className={classes.fab} onClick={this.handleAdd}>
        <AddIcon />
      </Fab>

      </form>
      <NotesListComponent value={this.state.value}/>
      </div>
    );
  }
}

InputNotes.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(InputNotes);
