import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
  },
  dense: {
    marginTop: 16,
  },
  menu: {
    width: 200,
  },
  button: {
    margin: theme.spacing.unit,
  }
});


class HomeComponent extends React.Component {
  constructor(props){  
    super(props)
  this.state = {
    name: '',
    Password: '',
    multiline: 'Controlled',
    currency: 'EUR',
  };
  this.handleNameChange=this.handleNameChange.bind(this)
  this.handlePasswordChange=this.handlePasswordChange.bind(this)
  this.handleOnSignUpClick=this.handleOnSignUpClick.bind(this)
  this.handleOnSignInClick=this.handleOnSignInClick.bind(this)
  this.handleUserSignInDetails=this.handleUserSignInDetails.bind(this)
  }
  handleNameChange (e){
    this.setState({
      name:e.target.value,
    });
  };

  handlePasswordChange (e){
    this.setState({
      Password:e.target.value,
    });
  };
  handleOnSignUpClick(e){
    this.props.handleUserDetails(this.state.name,this.state.Password)
    this.state.name=""
    this.state.Password=""
  }
  handleOnSignInClick(e){
    this.props.handleUserSignInDetails(this.state.name,this.state.Password)
    this.state.name=""
    this.state.Password=""
  }
  render() {
    const { classes } = this.props;

    return (
      <form className={classes.container} noValidate autoComplete="off">
        <TextField
          id="filled-name"
          label="Username"
          className={classes.textField}
          value={this.state.name}
          onChange={this.handleNameChange}
          margin="normal"
          variant="filled"
        />

        <TextField
          id="filled-uncontrolled"
          label="Password"
          defaultValue=""
          onChange={this.handlePasswordChange}
          value={this.state.Password}
          className={classes.textField}
          margin="normal"
          variant="filled"
        />
         <Button variant="contained" color="secondary" className={classes.button} onClick={this.handleOnSignInClick}>
        Login
      </Button>
      <Button variant="contained" color="secondary" className={classes.button} onClick={this.handleOnSignUpClick}>
        Sign Up
      </Button>
      </form>
    );
  }
}

HomeComponent.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(HomeComponent);
